import React from 'react';
import { useState,useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useHistory } from "react-router-dom";
import "../components/FontawsomeIcons"
import scrollreveal from "scrollreveal";
import { Form, Button } from 'semantic-ui-react';
import './Form.css';

import { useForm } from "react-hook-form";
const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
    const onSubmit = (data) => {
        console.log(data);
     
    
    }
  useEffect(() => {
    const sr = scrollreveal({
      origin: "bottom",
      distance: "80px",
      duration: 2000,
      reset: false,
    });
    sr.reveal(
      `
      .form-container 
    `,
      {
        opacity: 0,
        interval: 100,
      }
    );
  }, []);
  

  const history=useHistory();
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    useEffect(()=>
{
  if(localStorage.getItem("user-info")){
     
  }
},[])
async function login(){
  console.warn(username.password)
  const users={username,password}
  
  let item={username,password};
  let result= await fetch("http://localhost:8080/api/users/signin",{
      method:'POST',
      headers:{

          'Content-Type':'application/json',
          "Accept":"application/json",
          "Authorization":`Bearer`
         
      },
      body:JSON.stringify(item)
      
  })
  
  result =await result.json();
  localStorage.setItem("user-info",JSON.stringify(result))
  history.push("/dashboard")
}

  return (
    
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '90vh'
      }}
    >
      
        <div className='form-container'>
        <span className='close-btn'>Ã—</span>
        <div className='form-content-left'>
          <img className='form-img' src='img/bazra.svg' alt='.' />
        </div>
    <div className="form" >
    <form onSubmit={handleSubmit(onSubmit)}>
        <h2>Sign In</h2>

       
        <div className="form-inputs">
         <FontAwesomeIcon icon="user"/> 
          <label className="form-label">Email</label>
          <input
            className="form-input"
            type="username"
            name="username"
            placeholder="Enter your email"
  
            onChange={(e) => setUsername(e.target.value)}  {...register("username", { required: true })}
          />

          
        </div>
    
    
        <Form.Field>
        <div className="form-inputs">
        <FontAwesomeIcon icon="lock"/> 
          <label className="form-label">Password</label>
          <input
            className="form-input"
            type="password"
            name="password"
            placeholder="Enter your password"
          
            onChange={(e) => setPassword(e.target.value)}
          />
          <span className="form-input-login1">
            <a href="/email">Forgot password?</a>
          </span>
        </div>
        </Form.Field>

        <div className="form-inputs"></div>
        <Button className="form-input-btnx" type='sumbit' onClick={login}>
          Login
        </Button>
        </form>
        <span className="form-input-login">
          Dont't Hava an account? Register <a href="sign-up">here</a>
        </span>
      </div>
      
     
    </div>
    
    </div>
    
  );
};

export default Login;
