              <FaArrowCircleDown color='blue'/>
              </div>
              <div className="transaction__title__details">
    
              <h5 id='na1'  > <RiMoneyDollarCircleLine  color='blue'/>From Account:</h5><h5 id='na2'></h5>
              <h5 id='na1'><GiPayMoney color='green'/>To Account</h5>  <h5 id='na2'></h5>
                <h5 id='na1' ><GiTakeMyMoney color='red'/>Transaction Amount:</h5><h5 id='na2'></h5>
                <h5 id='na1'><FaCalendarDay color='purple' />Type:</h5><h5 id='na2'></h5>             
                <h5 id='na1'><FaStopwatch  color='#0a65a1' />Time:</h5> <h5 id='na2'></h5>
                
              </div>
          
            </div>
            <div className="transaction__amount">
            
         
          
       
       
           
            </div>
          </div>
          </div>
        );
   
         
    </div>
    
  </Section>
  )
}

