import React from "react";
import { useState, useEffect, useMemo  ,useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useHistory } from "react-router-dom";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
import "../components/FontawsomeIcons";
import scrollreveal from "scrollreveal";
import { Form, Button } from 'semantic-ui-react';
import swal from 'sweetalert';
import { useForm } from "react-hook-form";
import "./Form1.css";

import { faEye } from "@fortawesome/free-solid-svg-icons";
const eye = <FontAwesomeIcon icon={faEye} />;

const Signup = () => {
  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
  };
  const [passwordShown, setPasswordShown] = useState(false);
  const { register, handleSubmit, watch,formState: { errors } } = useForm();
    const onSubmit = (data) => {
        console.log(data);
        handleClick()
       }
       const passwordx = useRef({});
       passwordx.current = watch("passwordx", "");

  useEffect(() => {
    const sr = scrollreveal({
      origin: "top",
      distance: "80px",
      duration: 2000,
      reset: false,
    });
    sr.reveal(
      `
      .form-container 
    `,
      {
        opacity: 0,
        interval: 100,
      }
    );
  }, []);

  const history = useHistory();

  const login = () => {
    history.push("/ver");
  };
  const [agree, setAgree] = useState(false);
  const [firstName, setFirst_Name] = useState("");
  const [lastName, setLast_Name] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setRe_password] = useState("");
  const [country, setCountry] = useState("");
  const [birthDay, setBirth] = useState("");
  const [gender, setGender] = useState("");
  const [role, setRole] = useState("");
  const [phone, setPhone] = useState();
  const [app_user, setApp_user] = useState([]);
  const [username , setUsername] = useState("");

  const handleClick = (e) => {

    signupxx()



  const checkboxHandler = () => {
    // if agree === true, it will be set to false
    // if agree === false, it will be set to true
    // Don't miss the exclamation mark
  };
  async function signupxx(){

    console.warn(username.password)
    const userinfo = {
      birthDay,
      confirmPassword,
      country,
      email,
      firstName,
      gender,
      lastName,
      password,
      username,



    };

    let item={birthDay,
      confirmPassword,
      country,
      email,
      firstName,
      gender,
      lastName,
      password,
      username,};
   const options={
        method:'POST',
        headers:{

            'Content-Type':'application/json',
            "Accept":"application/json",


        },
        body:JSON.stringify(item)

    }
    const url =("http://localhost:8080/api/signup/user")
    try{
     const response= await fetch(url,options);
      const result =await response.json();
     console.log(result);
      localStorage.setItem("user-info",JSON.stringify(result))
      if(response.ok){
        console.log("Signup successful")
        swal("Successful", "Welcom", "success", {
          buttons: false,
          timer: 2000,
        })
        history.push("/login")
      }else{
        console.log("failed")
      swal("Failed To Signup", "Error", "error")
      }
      }catch(error) {
        console.error(error);
      }
    }
  }

  return (

    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '90vh'
      }}
    >

        <div className='form-containerxx'>


    <form className="form" onSubmit={handleSubmit(onSubmit)}>


        <div className="form-inputs">
            <label className="form-labelx">Phone Numer</label>
            <PhoneInput
              international
              countryCallingCodeEditable={true}

              type="text"
              name="phonex"
              value={username}
              {...register("phonex", {
                required: {
                  value: true,
                  message: "Please Enter your Phone number",
                },
                minLength: {
                  value: 10,
                  message: " must have at least 10 characters"
                      },
              })}

              onChange={setUsername}

            />

{errors.phonex && <p >{errors.phonex.message}</p>}
