};
  return (

    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '90vh'
      }}
    >
       <div className='form-container'>

        <form className="form">
        <h1>
         Sign Up
        </h1>

        <div className='form-inputs'>
          <label className='form-label'>Phone Numer</label>
          <PhoneInput

            international
            countryCallingCodeEditable={false}
            defaultCountry="ET"
            value={phone}
            onChange={setPhone}/>

        </div>
        <div className='form-inputs'>
        {/* country */}
        <FontAwesomeIcon icon="globe" className='fa' />
        <label className='form-label'>Country List</label>






        </div>
        <div className='form-inputs'>
        <FontAwesomeIcon icon="user" className='fa'/>     
          <label className='form-label'>First Name</label>

          <input
            className='form-input'
            type='text'
            name='first_name'
            placeholder='Enter your first_name'
            value={first_name}
            onChange={(e)=>setFirst_Name(e.target.value)}
          />

        </div>
        <div className='form-inputs'>
        <FontAwesomeIcon icon="user" className='fa'/>     
          <label className='form-label'>Last Name</label>

          <input
            className='form-input'
            type='text'
            name='last_name'
            placeholder='Enter your last_name'
            value={last_name}
            onChange={(e)=>setLast_Name(e.target.value)}
          />

        </div>
        <div className='form-inputs'>
        <FontAwesomeIcon icon="envelope" className='fa'/>  
          <label className='form-label'>Email</label>
          <input
            className='form-input'
            type='email'
            name='email'
            placeholder='Enter your email'
            value={email}
            onChange={(e)=>setEmail(e.target.value)}
          />

        </div>
        <div className='form-inputs' >
          <span className='form-input-icon'><FontAwesomeIcon icon="calendar" className='fa' /></span>

        <label className='form-label'>Birthdate</label>
        <input type='date'
        className='form-input'
         placeholder='Enter BirthDate'
         value={birth} onChange={(e)=>setBirth(e.target.value)}
          name='birthdate'
    />

        </div>

        <div className='form-inputs'>
        <FontAwesomeIcon icon="lock" className='fa'/> 
          <label className='form-label'>Password</label>
          <input
            className='form-input'
            type='password'
            name='password'
            placeholder='Enter your password'
            value={password}
            onChange={(e)=>setPassword(e.target.value)}
          />

        </div>
        <div className='form-inputs'>
        <FontAwesomeIcon icon="lock" className='fa'/> 
          <label className='form-label'>Confirm Password</label>
          <input
            className='form-input'
            type='password'
            name='re_password'
            placeholder='Confirm your password'
            value={re_password}
            onChange={(e)=>setRe_password(e.target.value)}
          />

        </div>

        <div className='form-inputs'>
        <input type="checkbox" id="agree" />
        <label htmlFor="agree"> I agree to <b>terms and conditions</b></label>
        </div>
        <button className='form-input-btn' type='submit' onClick={(e) => {login(); handleClick(e); }}>
          Sign up
        </button>
        <span className='form-input-login'>
          Already have an account? Login <a href='/login'>here</a>
        </span>
      </form>
    </div>
    </div>
  );
};

export default SignUp;
