import styled from 'styled-components'
import { FaMoneyBillAlt,FaMoneyCheckAlt} from "react-icons/fa";
import{GiTakeMyMoney,GiPayMoney,GiReceiveMoney,GiMoneyStack} from "react-icons/gi";
import { BsFillCalendar2WeekFill ,BsFillArrowUpCircleFill,BsFillArrowDownCircleFill} from "react-icons/bs";
import { IoStatsChart } from "react-icons/io5";
import{MdTransferWithinAStation} from "react-icons/md";

import React, { useState } from 'react';
import { NavLink as Link, NavLink } from 'react-router-dom';
import {RiMoneyDollarCircleLine } from "react-icons/ri";
import { BiGroup,BiTransfer } from "react-icons/bi";
import { FiActivity } from "react-icons/fi";
import { cardStyle } from '../components/ReusableStyles';
import { useHistory } from "react-router-dom";
import { Form, Button } from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import './error.css';
export default function TranSend() {
  const history=useHistory();
  const login = () => {
 
  };
  const [formData, setFormData] = useState({
    phone:"",
    amount: "",
    remark: ""
  });
  const handleSubmit2 = e =>
setFormData({
  ...formData,
  [e.target.name]: e.target.value
});
const { phone, amount,remark } = formData;
  const { register, handleSubmit, formState: { errors } } = useForm();
    const onSubmit = (data) => {
        console.log(data);
        history.push("/finish");
    }
  return (
   <Section>
     <NavLink style={{ textDecoration: 'none' }} to='/trans' activeStyle>  </NavLink>
  


     <form onSubmit={handleSubmit(onSubmit)}>
    
     <Form.Field>
  
   <h5>Send tox</h5>

<div className="analytic">

  <div className="content">
 
  </div>

  <input type="text" className= "form-input" placeholder='Enter phone number' name='phone' vlaue={phone}onChange={e => handleSubmit2(e)}
   {...register("firstName", { required: true, maxLength: 10 })}/>

</div>
</Form.Field>
{errors.firstName && <p className="text-error">*Entry Required</p>}
      <Form.Field>
<h5> Set Amount</h5>
<div className="analytic">
  <div className="content">
   
  </div>

  <input type="text" className= "form-input" placeholder='Enter Amount' name='amount' vlaue={amount}onChange={e => handleSubmit2(e)}
   {...register("lastName", { required: true, maxLength: 10 })}/>
  
</div>
</Form.Field>
{errors.lastName && <p className="text-error">*Entry Required</p>}
<Form.Field>
<h5>Remark</h5>
<div className="analytic">
  <div className="content">
    
  </div>
 
  <input type="text" className= "form-input" placeholder='Enter Payment Remark' name='remark' vlaue={remark}onChange={e => handleSubmit2(e)}
  {...register("fatherName", { required: true, maxLength: 10 })}
 />

  
</div>
</Form.Field>
{errors.fatherName && <p className="text-error">*Entry Required</p>}

<Link to={`/page2/${phone}/${amount}/${remark}`}>
<Button className="btn" type='submit' >Next</Button>
      </Link>
    
    
    </form>
   </Section>
  )
}
const Section= styled.section`
margin-left: 40vw;
padding: 2rem;
height: 100%;
display: grid;
  grid-template-columns: repeat(1, 1fr);
  gap: 1rem;

  .btn {
    box-shadow: 0 0 2px #080808;
    width: 20%;
    height: 50px;
    margin-left: 10vw;
    margin-top: 3vw;
  
    background: linear-gradient(
      90deg,
      rgb(251, 255, 39) 0%,
      rgb(251, 255, 39) 100%
    );
    outline: none;
    border: none;
    color: rgb(12, 12, 12);
    font-size: 1rem;
    border-radius: 20px;
  }
  .analytic {
    
      width: 600px;
      height:150px;
    display: inline-block;
    border: 1px solid grey;
    box-shadow: 0 5px 8px 0 rgba(0, 0, 0, 0.2), 0 7px 20px 0 rgba(0, 0, 0, 0.2);
    ${cardStyle};
    padding: 1rem;
    background-color: white;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    gap: 1rem;
    transition: 0.5s ease-in-out;
    border-radius: 1rem 1rem 1rem 1rem ;
    &:hover {
      
      background-color: #ffc107;
      color: black;
      svg {
        color: white;
      }
    }
    .logo {
      box-shadow: 0 5px 8px 0 rgba(0, 0, 0, 0.2), 0 7px 20px 0 rgba(0, 0, 0, 0.2);
      width: 600px;
      background-color: black;
      border-radius: 1rem;
      display: flex;
      justify-content: center;
      align-items: center;
      padding: 1.5rem;
      svg {
        font-size: 1.5rem;
      }
      
    }
}
@media screen and (min-width: 280px) and (max-width: 720px) {
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  .analytic {
    &:nth-of-type(3),
    &:nth-of-type(4) {
      flex-direction: row-reverse;
    }
  }
}`;
