import React from 'react'
import styled from "styled-components";
import { HiArrowNarrowRight } from "react-icons/hi";
import profile from "../assets/profile.jpeg";
import avatarImage from "../assets/avatarImage.jpeg";
import { cardStyle } from '../components/ReusableStyles';

export default function TraHistory() {
    const jwt =JSON.parse(localStorage.getItem('jwt'));
const summaryw=JSON.parse(localStorage.getItem('summary'));
     
     
    fetch("http://192.168.1.18:8080/api/accounts/transaction", {
  method: "GET",
   headers: {
    'Content-Type':'application/json',
          "Accept":"application/json",
          "Authorization":`Bearer ${jwt}`
  }
})
.then(response => response.json())
.then(response => {
    localStorage.setItem('summary', JSON.stringify(response['summary']));
    const summary = localStorage.getItem('summary')
      console.log(summary)
      
   
    
  this.setState({
    friends: response
    
  })
 
})
.catch(err => { console.log(err); 
});
  
  return (
      
   <Section>
   <NavLink style={{ textDecoration: 'none' }} to='/trans' activeStyle>  </NavLink>
   <form onSubmit={handleSubmit(onSubmit)}>

<div className="format">
   <h1>Transfer to Bank</h1>
 
 <h5>Select bank</h5>

<div className="content">
<select   styles={colourStyles}> <option value="grapefruit">Commerical Bank of Ethiopia</option>
          <option value="Awash International Bank.">Awash International Bank.</option>
          <option value="Dashen Bank.">Dashen Bank</option>
          <option value="Nib International Bank">Nib International Bank</option>
        </select>
</div>



<Form.Field>

<h5> Account number</h5>
<div className="analytic">
<div className="content">

</div>
<input type="text" className= "form-input" placeholder='Enter Account number'
  {...register("account", { required: true, maxLength: 10 , pattern: /^[0-9\b]+$/ })}
  onChange={(e) => setToAccountNumber(e.target.value)} 
  />

</div>
</Form.Field>
{errors.account && <p className="text-error">*Entry Required & only a number</p>}
<Form.Field>

<h5> Set Amount</h5>
<div className="analytic">
<div className="content">
 
</div>

<input type="text" className= "form-input" placeholder='Enter Amount'
 {...register("amount", { required: true, maxLength: 10 })}
 onChange={(e) => setAmount(e.target.value)} 
 />


</div>
</Form.Field>
{errors.amount && <p className="text-error">*Entry Required</p>}
<Form.Field>

<h5>Remark</h5>
<div className="analytic">
<div className="content">
  
</div>

<input type="text" className= "form-input" placeholder='Enter Remark'
 {...register("remark", { required: true, maxLength: 10 })}
 onChange={(e) => setMessage(e.target.value)} 
 />


</div>
</Form.Field>

{errors.remark && <p className="text-error">*Entry Required</p>}

  <Button type='submit' className="btn" onClick={() => {
    swal({
title: "Are you sure?",
text: "Once you Send it, you will not be able to recover your Money",
icon: "warning",
buttons: true,
dangerMode: true,
})
.then((willDelete) => {
if (willDelete) {
  swal(`The returned value is: ${login()}`, {
    icon: "success",
  });
} else {
  swal("You Canceled your Transfer!");
}
});
}} > Next</Button>

</div>

</form>
 </Section>
  )
}

const Section= styled.section`
${cardStyle};
margin-left: 40vw;
margin-top: 1vw;
padding: 2rem;
width: 600px;
height:750px;
box-shadow: 0 5px 8px 0 rgba(0, 0, 0, 0.2), 0 7px 20px 0 rgba(0, 0, 0, 0.2);
display: flex;
flex-direction: column;
gap: 1rem;
.title {
  h2 {
    color: #ffc107;
    font-family: "Permanent Marker", cursive;
    letter-spacing: 0.3rem;
  }
}
.transactions {
  
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-top: 1rem;
  .transaction {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    &__title {
      display: flex;
      gap: 1rem;
      &__image {
        img {
          height: 2.5rem;
          border-radius: 3rem;
        }
      }
      &__details #na1 , #na2{
        color:  #ffc107;
        display: inline-block;
        padding:9px 4px;
      }
     
    }
    &__amount {
      background-color: #d7e41e1d;
      padding: 0.2rem 0.5rem;
      width: 20rem;
      border-radius: 1rem;
      text-align: center;
      transition: 0.3s ease-in-out;
      &:hover {
        background-color: #ffc107;
        span {
          color: black;
        }
      }
      span {
        color: #ffc107;
      }
    }
  }
}
.view {
  width: 100%;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  text-decoration: none;
  color: #ffc107;
  font-weight: bold;
  margin-top: 1rem;
  gap: 0.5rem;
  svg {
    transition: 0.3s ease-in-out;
    font-size: 1.4rem;
  }
  &:hover {
    svg {
      transform: translateX(0.5rem);
    }
  }
}
@media screen and (min-width: 280px) and (max-width: 375px) {
  .transactions {
    .transaction {
      flex-direction: column;
      align-items: center;
      gap: 1rem;
    }
  }
}
`;
